import React, {useState} from "react"
import "./login.css"
import axios from "axios"
import { useHistory,Link } from "react-router-dom"
// import forgotpassword from "../register/forgotpassword"


const Login = ({ updateUser}) => {

    const history = useHistory()

    const [ user, setUser] = useState({
        email:"",
        password:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:8000/login", user)
        .then(res => {
            alert(res.data.message)
            updateUser(res.data.user)
            history.push("/")
        })
    }

    <div className="l1">
    <Link to="/forgotpassword" className="">
      Forgot Password?
    </Link>
    </div>

    return (
    //     <div className="l1">
    //         <h1>Login</h1>
    //         <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
    //         <input type="password" name="password" value={user.password} onChange={handleChange}  placeholder="Enter your Password" ></input>
    //         <div className="button" onClick={login}>Login</div>
    //         <div className="text-right">
    //         <div className="button" onClick={forgotpassword}>
    // <Link to="/forgotpassword" className="">
    //   Forgot Password?
    // </Link>
    // </div>
    // </div>
    //         <div>or</div>
    //         <div className="button" onClick={() => history.push("/register")}>Register</div>
    //     </div>


    <div className="auth-form-container">
    <h2>Login</h2>
    <form className="login-form" onClick={login}>
        <label htmlFor="email">email</label>
        <input value={user.email} onChange={handleChange}type="email" placeholder="youremail@gmail.com" id="email" name="email" />
        <label htmlFor="password">password</label>
        <input value={user.password} onChange={handleChange} type="password" placeholder="********" id="password" name="password" />
        <button type="submit">Log In</button>
    </form>
    <button className="link-btn" onClick={() => history.push("/register")}>Don't have an account? Register here.</button>
</div>
    )
}

export default Login
