import React, {useState} from "react"
import "./login.css"
import axios from "axios"
import { useHistory,Link } from "react-router-dom"
// import forgotpassword from "../register/forgotpassword"


const Login = ({ updateUser}) => {

    const history = useHistory()

    const [ user, setUser] = useState({
        email:"",
        password:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:8000/login", user)
        .then(res => {
            alert(res.data.message)
            updateUser(res.data.user)
            history.push("/")
        })
    }

    <div className="text-right">
    <Link to="/forgotpassword" className="">
      Forgot Password?
    </Link>
    </div>

    return (
    //     <div className="login">
    //         <h1>Login</h1>
    //         <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
    //         <input type="password" name="password" value={user.password} onChange={handleChange}  placeholder="Enter your Password" ></input>
    //         <div className="button" onClick={login}>Login</div>
    //         <div className="text-right">
    //         <div className="button" onClick={forgotpassword}>
    // <Link to="/forgotpassword" className="">
    //   Forgot Password?
    // </Link>
    // </div>
    // </div>
    //         <div>or</div>
    //         <div className="button" onClick={() => history.push("/register")}>Register</div>
    //     </div>

    <form>
  <div class="form-group">
    <label for="exampleInputEmail1" >Email address</label>
    <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" onChange={handleChange}/>
  </div>
  <div class="form-group">
    <label for="exampleInputPassword1">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password"onChange={handleChange}/>
  </div>

  <div className="button" class="btn btn-primary" onClick={login}>Login</div>
    <div className="button" class="btn btn-primary" onClick={() => history.push("/register")}>Register</div>
</form>
    )
}

export default Login
