import React, { Component } from 'react'
import NewsItem from './NewsItem'
import PropTypes from 'prop-types'


export default class News extends Component {
    static defaultProps = {
        country:'in',
        pageSize: 18,
        category:'general'

    }
    PropTypes = {
        country :PropTypes.string,
        category:PropTypes.string,
        pageSize: PropTypes.number,

    }
    constructor(){
        super();
        console.log("Hello ");
        this.state={
            articles:[],
            loading:false,
            page:1

        }
    }
   async componentDidMount(){
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&page=${this.state.page}&pageSize=${this.props.pageSize}&apiKey=58b83ba69a6a43edac5cfbf3dd194f62`;
        let data = await fetch(url);
        let parseData = await data.json()
        console.log(parseData);
        this.setState({articles:parseData.articles,totoalResults:parseData.totoalResults});
    }

    handlePrevClick= async()=>{
        console.log("Hello");
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&page=${this.state.page-1}&pageSize=${this.props.pageSize}&apiKey=58b83ba69a6a43edac5cfbf3dd194f62`;
        let data = await fetch(url);
        let parseData = await data.json()
        console.log(parseData);

        this.setState({
            page:this.state.page-1,
            articles:parseData.articles
        })

    }

    handleNextClick= async()=>{
        if(this.state.page+1>Math.ceil(this.state.totoalResults/this.props.pageSize))
        {

        }
        else{
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&page=${this.state.page+1}&pageSize=${this.props.pageSize}&apiKey=58b83ba69a6a43edac5cfbf3dd194f62`;
        let data = await fetch(url);
        let parseData = await data.json()
        console.log(parseData);

        this.setState({
            page:this.state.page+1,
            articles:parseData.articles
        })
    }

    }


    render() {
        return (
            <div className='container my-3'>
                <h1 className='text-center'>Top Headlines</h1>
                <div className='row'>
                    {this.state.articles.map((element)=>{
                        return <div className='col-md-4' key={element.url}>
                        <NewsItem title={element.title?element.title.slice(0,45):""} description={element.description?element.title.slice(0,88):""} imageUrl={element.urlToImage} newsUrl={element.url} />
                    </div>

                    })}
                    {/* }}} */}
                   </div>
                   <div className="container d-flex justify-content-between">
                   <button disabled={this.state.page <= 1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}><span aria-hidden="true">&larr;</span>Previous</button>
                   <button disabled={this.state.page + 1 > Math.ceil(this.state.totoalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}><span aria-hidden="true">&rarr;</span>Next</button>


            </div>
            </div>

        )
    }
}
