    //         <div className="button" onClick={() => history.push("/register")}>Register</div>
    //     </div>

<body>

	<div class="main">  	
		<input type="checkbox" id="chk" aria-hidden="true"/>




				<form>
                <div class="login">
					<label for="chk" aria-hidden="true">Login</label>
					<input type="email" name="email" placeholder="Email" required="" value={user.name} onChange={handleChange}><h1>Enter Email</h1></input>
					<input type="password" name="pswd" placeholder="Password" required="" value={user.email} onChange={handleChange}></input>
					<button onClick={login}>Login</button>
                    </div>
				</form>

	</div>
