import React, {useState} from "react"
import "./login.css"
import axios from "axios"
import { useHistory,Link } from "react-router-dom"
// import forgotpassword from "../register/forgotpassword"


const Login = ({ updateUser}) => {

    const history = useHistory()

    const [ user, setUser] = useState({
        email:"",
        password:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:8000/login", user)
        .then(res => {
            alert(res.data.message)
            updateUser(res.data.user)
            history.push("/")
        })
    }

    <div className="l1">
    <Link to="/forgotpassword" className="">
      Forgot Password?
    </Link>
    </div>

    return (
    //     <div className="login">
    //         <h1>Login</h1>
    //         <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
    //         <input type="password" name="password" value={user.password} onChange={handleChange}  placeholder="Enter your Password" ></input>
    //         <div className="button" onClick={login}>Login</div>
    //         <div className="text-right">
    //         <div className="button" onClick={forgotpassword}>
    // <Link to="/forgotpassword" className="">
    //   Forgot Password?
    // </Link>
    // </div>
    // </div>
    //         <div>or</div>
    //         <div className="button" onClick={() => history.push("/register")}>Register</div>
    //     </div>



    <div class="form login">
        <header>Login</header>
        <form action="/login">
          <input type="text" placeholder="Email address" required value={user.email} onChange={handleChange}/>
          <input type="password" placeholder="Password" value={user.password} onChange={handleChange}required />
          <a href="/">Forgot password?</a>
          <input type="submit" value="Login" onClick={login} />
        </form>
      </div>

    )
}

export default Login
