    //   Forgot Password?
    // </Link>
    // </div>
    // </div>
    //         <div>or</div>
    //         <div className="button" onClick={() => history.push("/register")}>Register</div>
    //     </div>



	<div class="main">  	
		{/* <input type="checkbox" id="chk" aria-hidden="true"> */}




				<form>
                <div class="login">
					<label for="chk" aria-hidden="true">Login</label>
					<input type="email" name="email" placeholder="Email" required="" value={user.name} onChange={handleChange}/>
					<input type="password" name="pswd" placeholder="Password" required="" value={user.email} onChange={handleChange}/>
					<button onClick={login}>Login</button>
                    </div>
				</form>

	</div>


    )
}

export default Login
