import React, {useState} from "react"
import "./login.css"
import axios from "axios"
import { useHistory,Link } from "react-router-dom"
// import forgotpassword from "../register/forgotpassword"


const Login = ({ updateUser}) => {

    const history = useHistory()

    const [ user, setUser] = useState({
        email:"",
        password:""
    })

    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const login = () => {
        axios.post("http://localhost:8000/login", user)
        .then(res => {
            alert(res.data.message)
            updateUser(res.data.user)
            history.push("/")
        })
    }

    <div className="l1">
    <Link to="/forgotpassword" className="">
      Forgot Password?
    </Link>
    </div>

    return (
    //     <div className="login">
    //         <h1>Login</h1>
    //         <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
    //         <input type="password" name="password" value={user.password} onChange={handleChange}  placeholder="Enter your Password" ></input>
    //         <div className="button" onClick={login}>Login</div>
    //         <div className="text-right">
    //         <div className="button" onClick={forgotpassword}>
    // <Link to="/forgotpassword" className="">
    //   Forgot Password?
    // </Link>
    // </div>
    // </div>
    //         <div>or</div>
    //         <div className="button" onClick={() => history.push("/register")}>Register</div>
    //     </div>
    <div class="padding container d-flex justify-content-center">
    <div class="col-md-10 col-md-offset-1">
        <form class="signup-form">
            <h2 class="text-center">Login</h2>
            <hr/>
            {/* <div class="form-group"> <input type="text" class="form-control" placeholder="Full Name" required="required"/> </div> */}
            <div class="form-group"> <input type="text" class="form-control" name="email"  required="required"  value={user.email} onChange={handleChange} placeholder="Enter your Email"></input></div>
{/* <div class="form-group"> <input type="email" class="form-control" placeholder="Email Address" required="required"/> </div> */}
 <div class="form-group"> <input type="password" name="password" class="form-control" required="required" value={user.password} onChange={handleChange}  placeholder="Enter your Password" ></input></div>
 <div className="form-group text-center"><div className="button" onClick={login}>Login</div></div>
              {/* <div class="form-group text-center"> <button type="submit" class="btn btn-blue btn-block">Start Now</button> </div> */}
              <div>or</div>
              <div class="form-group">  <div className="button" onClick={() => history.push("/register")}>Register</div></div>
        </form>
    </div>
</div>

    )
}

export default Login
